using System; using UnityEngine; using UnityEngine.UI; using System.Collections; using UnityEngine.EventSystems; using System.Collections.Generic;  public class CursorScript : MonoBehaviour, IPointerEnterHandler, IPointerExitHandler { 	public Texture2D cursorTextureForBlocNotes, cursorTextureForButton, cursorTextureForData;  	Vector2 hotSpot = Vector2.zero;  	CursorMode cursorMode = CursorMode.Auto;  	private GameObject testSubject;  	private string curseurBlocNotes = "curseur4", curseurBouton = "curseur_1", curseurData = "curseur3";  	void Start()  	{ 		testSubject = GameObject.Find ("PlainImage");  		UnityEngine.Object[] sprites = Resources.LoadAll ("Resources", typeof(Texture2D));   //		Debug.Log (sprites.Length);   		for (int i = 0; i < sprites.Length; i++)  		{ 			if (sprites[i].name == curseurBlocNotes)  			{ 				cursorTextureForBlocNotes = sprites [i] as Texture2D; 			} 			else if (sprites[i].name == curseurBouton) 			{ 				cursorTextureForButton = sprites[i] as Texture2D;    			} 			else if (sprites[i].name ==  curseurData)  			{ 				cursorTextureForData = sprites[i] as Texture2D;  			} 		} 	}  	public void OnPointerEnter (PointerEventData eventData) 	{ 		if (eventData.pointerEnter.gameObject.GetComponent<Button> () != null)  		{ 			if (true)  			{ 				Cursor.SetCursor (cursorTextureForButton, hotSpot, cursorMode); 			} 			else if (eventData.pointerEnter.gameObject.GetComponent<NewData>() != null) 			{ 				Cursor.SetCursor (cursorTextureForData, hotSpot, cursorMode); 			} 		}  		else 		{ 			Cursor.SetCursor (cursorTextureForBlocNotes, hotSpot, cursorMode);  		} 	}  	public void OnPointerExit(PointerEventData onPointerExitEventData)  	{ 		Cursor.SetCursor (null, hotSpot, cursorMode); 	} }