using System; using UnityEngine; using UnityEngine.UI; using System.Collections; using UnityEngine.EventSystems; using System.Collections.Generic;  public class CursorScript : MonoBehaviour, IPointerEnterHandler, IPointerExitHandler { 	Vector2 hotSpot = Vector2.zero;  	CursorMode cursorMode = CursorMode.Auto;  	GameObject target;  	void Start()  	{ 		target = GameObject.Find ("TestCursor"); 	}  	public void OnPointerEnter (PointerEventData eventData) 	{ 		if (gameObject.name == "DropZone")  		{ 			target.GetComponent<Image> ().sprite = target.GetComponent<MousePosition> ().curTxt_BlocNotes;  			Debug.Log ("Yay! DropZone is True."); 		} 		else if (gameObject.GetComponent<Animator> () == true && gameObject.GetComponent<DataPrefab> () == true)  		{ 			target.GetComponent<Image> ().sprite = target.GetComponent<MousePosition> ().curTxt_DataIN;  			Debug.Log ("Le curseur est sur une DATA, qui elle meme est dans le BLOC NOTES: " + name); 		} 		else if (gameObject.GetComponent<Button> () == true && gameObject.GetComponent<NewData> () == true)  		{ 			target.GetComponent<Image> ().sprite = target.GetComponent<MousePosition> ().curTxt_DataOUT;  			Debug.Log ("Le curseur est sur un BOUTTON, qui declenche les DATA: " + name); 		} 		else if (gameObject.GetComponent<Button> () == true)  		{ 			target.GetComponent<Image> ().sprite = target.GetComponent<MousePosition> ().curTxt_Button;  			//target.GetComponent<MousePosition>().m_Animator.SetBool("isClickable", true);  			Debug.Log ("Le curseur est sur un BOUTTON: " + name); 		} 	}  	public void OnPointerExit(PointerEventData onPointerExitEventData)  	{ 		//target.GetComponent<MousePosition>().m_Animator.SetBool("isClickable", false);  		target.GetComponent<Image> ().sprite = target.GetComponent<MousePosition> ().startImage; 		 		//Cursor.SetCursor (null, hotSpot, cursorMode); 	}  	public void test() 	{ 		StartCoroutine (Testing()); 	}  	IEnumerator Testing() 	{ 		target.GetComponent<MousePosition>().m_Animator.SetBool("isClickable", true);  		yield return new WaitForSeconds (.5f);  		target.GetComponent<MousePosition>().m_Animator.SetBool("isClickable", false); 		//target.GetComponent<MousePosition> ().m_Animator.SetTrigger ("hasBeenClicked"); 	} }