using System.Collections; using System.Collections.Generic; using UnityEngine; using UnityEngine.UI; using UnityEngine.SceneManagement;  public class FadeInScript : MonoBehaviour {   	public Image imageFin; 	public GameObject TextEnd; 	public GameObject BQuitter; 	public GameObject BRetourMenu;   	// Use this for initialization 	void Start () {  	}  	// Update is called once per frame 	void Update () {  	}   	IEnumerator FadeIn ()  	{ 		for (float i = 0; i <= 1; i += Time.deltaTime) 		{  			imageFin.color = new Color(1, 1, 1, i); 			yield return null;  		} 	}    	public void startFadingIn () 	{  		imageFin.gameObject.SetActive (true);  		StartCoroutine ("FadeIn");  		TextEnd.SetActive (true);  		BRetourMenu.SetActive (true);  		BQuitter.SetActive (true);   	} }  
