using System.Collections; using System.Collections.Generic; using UnityEngine;  public class SoundClickScript : MonoBehaviour { 	public AudioClip[] soundClickList, soundtypingList; //list sound mouse clikcs and typing  	AudioSourceManagerScript ASMS_ClickAndTyping;  	void Start ()  	{ 		ASMS_ClickAndTyping = this.gameObject.GetComponent<AudioSourceManagerScript> (); 	}  	void FixedUpdate ()  	{ 		if (Input.GetMouseButtonDown (0)) 		{ 			MouseMakesNoises (); 		}  		if (Input.anyKeyDown) 		{ 			KeyboardMakesNoises (); 		}  //		if (ASMS_ClickAndTyping.audioSourceClicksEtTyping.isPlaying == true /*&& (ASMS_ClickAndTyping.audioSourceClicksEtTyping.time == ASMS_ClickAndTyping.audioSourceClicksEtTyping.clip.length)*/) //		{ //			Debug.Log ("L'audioClip " + ASMS_ClickAndTyping.audioSourceClicksEtTyping.clip.name + " a terminer de jouer"); //		} 	}  	void MouseMakesNoises() 	{ 		ASMS_ClickAndTyping.audioSourceClicksEtTyping.PlayOneShot (soundClickList [Random.Range (0,soundClickList.Length)]); 	}  	void KeyboardMakesNoises() 	{ 		if (Input.GetMouseButton (0) || Input.GetMouseButton (1) || Input.GetMouseButton (2) ) 		{ 			return; 		}  		StartCoroutine (ChangeVolumeValue());  //		ASMS_ClickAndTyping.audioSourceMusique.volume = .66f; // //		ASMS_ClickAndTyping.audioSourceClicksEtTyping.PlayOneShot (soundtypingList [Random.Range (0,soundtypingList.Length)]);  //		ASMS_ClickAndTyping.FadeSoundWhenClickIsPlaying ();  //		ASMS_ClickAndTyping.ResetAllAudioSourcesVolumeSliders (); 	}  	IEnumerator ChangeVolumeValue() 	{ 		ASMS_ClickAndTyping.audioSourceMusique.volume = 0.66f;  		ASMS_ClickAndTyping.audioSourceClicksEtTyping.PlayOneShot (soundtypingList [Random.Range (0,soundtypingList.Length)]);  		yield return new WaitForSeconds (0.035f);  //		ASMS_ClickAndTyping.ResetAllAudioSourcesVolumeSliders (); 	} }