using System.Collections; using System.Collections.Generic; using UnityEngine; using UnityEngine.Audio; using UnityEngine.UI;  public class SoundDesignScript : MonoBehaviour  { 	public AudioClip  					sound_courriel, 				//AudioClip Sound Courriel Application 					sound_trash,					//AudioClip Sound Trash Application 					sound_browser, 					//AudioClip Sound Browser Application 					sound_itunes, 					//AudioClip Sound Itunes Application 					sound_browser_error, 			//AudioClip sound Error Browser 					sound_browser_right, 			//AudioClip sound bonne adresse ajoute Browser 					sound_tl_connexion, 			//AudioClip sound bon mot de passe connexion 					sound_tl_wrong, 				//AudioClip sound mauvais mot de passe 					sound_tl_hacking,				//AudioClip sound recuperation du compte TL tonlivre effectué 					sound_tl_right, 				//AudioClip sound bonne réponse  					sound_starting_mac,  			//AudioClip starting mac  					sound_notification_courriel; 	//AudioClip notification Courriel  	AudioSourceManagerScript ASMS_Boutons;  	void Start () 	{ 		ASMS_Boutons = this.gameObject.GetComponent<AudioSourceManagerScript> (); 	}  	//OnClickBarreAppli 	public void OnclickSoundCourriel ()  	{ 		ASMS_Boutons.audioSourceBoutons.PlayOneShot (sound_courriel); 	}  	public void OnclickSoundTrash ()  	{ 		ASMS_Boutons.audioSourceBoutons.PlayOneShot (sound_trash); 	}  	public void OnclickSoundItunes ()  	{ 		ASMS_Boutons.audioSourceBoutons.PlayOneShot (sound_itunes); 	}  	public void OnclickSoundBrowser ()  	{ 		ASMS_Boutons.audioSourceBoutons.PlayOneShot (sound_browser); 	} 		 	//OnclickBrowser 	public void OnclickSoundBrowserError ()  	{ 		ASMS_Boutons.audioSourceBoutons.PlayOneShot (sound_browser_error); 	}  	public void OnclickSoundBrowserRight ()  	{ 		ASMS_Boutons.audioSourceBoutons.PlayOneShot (sound_browser_right); 	}  	//OnClickTonLivre 	public void OnclickSoundTLConnexion ()  	{ 		ASMS_Boutons.audioSourceBoutons.PlayOneShot (sound_tl_connexion); 	}  	public void OnclickSoundTLWrong () 	{ 		ASMS_Boutons.audioSourceBoutons.PlayOneShot (sound_tl_wrong); 	}  	public void OnclickSoundTLHacking ()  	{ 		ASMS_Boutons.audioSourceBoutons.PlayOneShot (sound_tl_hacking); 	}  	public void OnclickSoundTLRight ()  	{ 		ASMS_Boutons.audioSourceBoutons.PlayOneShot (sound_tl_right); 	} 		 	//introduction et début du jeu 	public void OnClicksoundDemarrageMac ()  	{ 		ASMS_Boutons.audioSourceBoutons.PlayOneShot (sound_starting_mac); 	}  	public void OnClickSoundNotificationCourriel ()  	{ 		ASMS_Boutons.audioSourceBoutons.PlayOneShot (sound_notification_courriel); 		ASMS_Boutons.audioSourceBoutons.PlayDelayed (1.7f); 	} }